/*\
title: date.js
type: application/javascript
module-type: macro

Macro to return a formatted version for a given time

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "date";

exports.params = [
	{name: "format"},
	{name: "year"},
	{name: "month"},
	{name: "day"},
	{name: "h"},
	{name: "mn"},
	{name: "sec"},
	{name: "ms"}
];

/*
Run the macro
*/
exports.run = function(format, year, month, day, h, mn, sec, ms) {
    var d = new Date(year, month - 1, day, h?h:0, mn?mn:0, sec?sec:0, ms?ms:0);
	return $tw.utils.formatDateString(d, format || "0hh:0mm, DDth MMM YYYY");
};

})();

