/*\
title: simplecalendarUtils.js
type: application/javascript
module-type: library
Internal utility functions for calendars.
\*/
(function () {

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

// Calendar

    function calendarArray (year, month, startingDay) {
        var firstOfMonth = new Date(year, month - 1, 1);
        var nbDaysInMonth = numberDaysInMonth(year, month);
        var daysNumArray = [0, 1, 2, 3, 4, 5, 6];
        var currDay = 0;
        var w = [];
        var ymarr = [];
        var nbComplWeeks;
        var rem;
        var nbPrevious = nbDaysOfPreviousMonth(startingDay, firstOfMonth.getDay());
        rotateArray(startingDay, daysNumArray);
        w[0] = [];
        for (var i = 0; i < nbPrevious; i++) {
            ymarr = previousMonth(year, month);
            calendarArrayCell(w, ymarr[0], ymarr[1], numberDaysInMonth(ymarr[0], ymarr[1])-nbPrevious+i+1, daysNumArray, 0, i)
        }
        for (var i = nbPrevious; i < 7; i++) {
            currDay += 1;
            calendarArrayCell(w, year, month, currDay, daysNumArray, 0, i)
        }
        nbComplWeeks = Math.floor((nbDaysInMonth-currDay)/7);
        for (i = 0; i < nbComplWeeks; i++) {
            w[i+1] = [];
            for (var j = 0; j < 7; j++) {
                currDay += 1;
                calendarArrayCell(w, year, month, currDay, daysNumArray, i + 1, j)
            }
        }
        rem = nbDaysInMonth - currDay;
        if (rem > 0) {
            w[nbComplWeeks+1] = [];
            for (var i = 0; i < rem; i++) {
                currDay += 1;
                calendarArrayCell(w, year, month, currDay, daysNumArray, nbComplWeeks + 1, i)
            }
            if ((7 - rem) > 0) {
                currDay = 0;
                for (var i = rem; i < 7; i++){
                    currDay += 1;
                    ymarr = nextMonth(year, month);
                    calendarArrayCell(w, ymarr[0], ymarr[1], currDay, daysNumArray, nbComplWeeks + 1, i)
                }
            }
        }
        return w;
    }

    function calendarArrayCell(calArray, year, month, currDay, daysNumArray, i, j) {
        calArray[i][j] = [];
        calArray[i][j][0] = year;
        calArray[i][j][1] = month;
        calArray[i][j][2] = currDay;
        calArray[i][j][3] = daysNumArray[j];
    }

    function numberDaysInMonth(year, month) {
        var days;
        if (month == 1 || month == 3 || month == 5 || month == 7 || month == 8 || month == 10 || month == 12)  days=31;
        else if (month == 4 || month == 6 || month == 9 || month == 11) days=30;
        else if (month == 2)  {
            if (isLeapYear (year)) {
                days=29;
            }
            else {
                days=28;
            }
        }
        return days;
    }

    function isLeapYear(year) {
        if (((year % 4)==0) && ((year % 100)!=0) || ((year % 400)==0)) {
            return true;
        } else {
            return false;
        }
    }

    function previousMonth(year, month) {
        var arr = [];
        if (month == 1) {
            arr[1] = 12;
            arr[0] = year - 1;
        } else {
            arr[1] = month - 1;
            arr[0] = year;
        }
        return arr;
    }

    function nextMonth(year, month) {
        var arr = [];
        if (month == 12) {
            arr[1] = 1;
            arr[0] = year + 1;
        } else {
            arr[1] = month + 1;
            arr[0] = year;
        }
        return arr;
    }

    function nbDaysOfPreviousMonth(startingDay, firstDay) {
        var nbz = firstDay - startingDay;
        return (nbz >= 0)? nbz : nbz + 7;
    }

    function rotateArray(num, arr) {
        var d;
        for (var i = 0; i<num; i++) {
            d = arr.shift();
            arr.push(d);
        }
//        return a;
    }

    exports.calendarArray = calendarArray;
    exports.rotateArray = rotateArray;

    })();

