/*\
title: calendarCellCSSMacro.js
type: application/javascript
module-type: macro

<<calendarCellCss name year month day dayNum>>

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro

*/

var CSSFunctions = require("$:/plugins/frd/simplecalendar/calendarCellCSSFunctions.js").CSSFunctions;

exports.name = "calendarCellCSS";

exports.params = [
	{ name: "name" },
	{ name: "year" },
	{ name: "month" },
	{ name: "day" },
	{ name: "dayNum" }
];

/*
Run the macro
*/
exports.run = function calendarCellCSS (name, year, month, day, dayNum) {
	var f, l, item, style, datePickerDate, currTid;
	var classes=[];
    var res;
// Dealing with the DaysItems
	var arr = this.wiki.getTiddlersWithTag(name);
    l = arr.length;
    for (var i = 0;i < l;i++) {
        item = this.wiki.getTiddlerData(arr[i],"erreur");
        if (item === "erreur") return "Error in CalendarDaysItem : " + arr[i];
        f = CSSFunctions[item["function"]];
        if (typeof f === "function") {
            if (f(year, month, day, dayNum, item)) {
//                style = this.wiki.getTiddlerData(item["style"],"erreur");
//                if (style === "erreur") return "Error in CalendarDaysStyle : " + item["style"];
                classes.push("calendar-" + item["style"]);
            }
        }
    }
// Dealing with the datePicker CSS function

    if (this.getVariable("datePicker") == "yes") {
        datePickerDate = this.getVariable("datePickerDate");
        f = CSSFunctions["datepicker"];
        if (f(year, month, day, dayNum, datePickerDate)) classes.push("calendar-datepicker-cell");
    }

    return classes.join(" ");
//    return "fonction : " + item["function"] + "  res : " + res;
}

})();

/*\
title: calendarCellCSSFunctions.js
type: application/javascript
module-type: library

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this library

*/

var CSSFunctions={

    alldays: function(year, month, day, dayNum, item) {
        return true;
    },

	restdays: function(year, month, day, dayNum, item) {
        var arr = item["listOfDaysNumbers"].split(" ");
        var l = arr.length;
        var result = false;
        for (var i = 0; i < l ; i++) {
            if (parseInt(arr[i], 10) == parseInt(dayNum, 10)) result = true;
        }
		return result;
	},

	holidays: function(year, month, day, dayNum, item){
        var arr = item["listOfDays"].split(" ");
		var date = year + "-" + $tw.utils.pad(month) + "-" +$tw.utils.pad(day);
		if (arr.indexOf(date) !== -1) return true;
		else return false;
	},

    holidaysRange: function(year, month, day, dayNum, item) {
		var currentDate = new Date(year, month - 1, day);
        var startDateArray = item["startingDay"].split("-");
        var startDate = new Date(startDateArray[0], startDateArray[1] - 1, startDateArray[2]);
        var endDateArray = item["endingDay"].split("-");
        var endDate = new Date(endDateArray[0], endDateArray[1] - 1, endDateArray[2]);
        if ((currentDate >= startDate) && (currentDate <= endDate)) return true;
        else return false;
    },

	today: function(year, month, day, dayNum, item) {
		var d = new Date();
		var y = d.getFullYear();
		var m = d.getMonth()+1;
		var dd = d.getDate();
		var todaydate = y + "-" + $tw.utils.pad(m) + "-" + $tw.utils.pad(dd);
		var currentdate = year + "-" + $tw.utils.pad(month) + "-" + $tw.utils.pad(day);
		if(todaydate === currentdate) return true;
		else return false;
	},

    datepicker: function(year, month, day, dayNum, date) {
        var d = new Date(year, month - 1, day);
        var format = $tw.wiki.getTiddlerText("$:/config/NewJournal/Title");
        return ($tw.utils.formatDateString(d, format || "DDth MMM YYYY") == date);
    }
}

exports.CSSFunctions = CSSFunctions;


})();

