/*\
title: simplecalendarTable.js
type: application/javascript
module-type: macro

    author : Fran√ßois Deblon, francois.deblon@yahoo.fr

<<simplecalendarTable name yearState monthState startingDay yearInit monthInit config>>

\*/
(function () {

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro

*/

    var utils = require("$:/plugins/frd/simplecalendar/simplecalendarUtils.js");

    exports.name = "simplecalendarTable";
    exports.params = [
        { name: "name" },
        { name: "yearState" },
        { name: "monthState" },
        { name: "startingDay" },
        { name: "yearInit" },
        { name: "monthInit" },
        { name: "config" }
    ];

/*
Run the macro
*/

/*  Displays a monthly calendar.
    @params :
				- name : 
				- yearState and monthState are textReferences (see tiddlywiki.com)
                like "tiddler" (the field is the text field), "!!field" 
                (the tiddler is the current tiddler), or "tiddler!!field"
                - startingDay is a number coding a day :
                "0" for sunday, "1" for monday, ..., "6" for saturday
                - yearInit and monthInit are init values for the year or month
                - config is a json tiddler for more options (names of days, months,
                choice of functions for cell content, css, ...)
				-b format : 
    @return :   a string of mixed html and wikitext representing a monthly calendar

*/

    exports.run = function simplecalendarTable(name, yearState, monthState, startingDay,
                                          yearInit, monthInit, config) {

        var w, l, conf, days, arr;
        var str = "", pyear = "", pmonth = "", parsedYear = "", parsedMonth = "";

        //  Load the config tiddler ; if not specified, the tiddler "calendarConfigDefault" is used
        conf = this.wiki.getTiddlerData(config,"error");
        if (conf === "error") return "Error config";
        else {
			if (conf.format == "default") conf.format = this.wiki.getTiddlerText("$:/config/NewJournal/Title");
            //  Parse the states (year and month) if any
            if (yearState) {
                parsedYear = $tw.utils.parseTextReference(yearState);   //  returns an object
                pyear = getValueFromParsed(this, parsedYear);           //  returns the value of the state
				if (pyear == "no tiddler") return "The state tiddler for the year does not exist ; create it before calling the simplecalendar macro";
            }                                                           //  (as a string)
            if(monthState) {
                parsedMonth = $tw.utils.parseTextReference(monthState); //  idem for the month
                pmonth = getValueFromParsed(this, parsedMonth);
				if (pmonth == "no tiddler") return "The state tiddler for the month does not exist ; create it before calling the simplecalendar macro";
            }
            //  Get the numerical values of year, month and startingDay.
            //  Return an array containing the numerical values for year, month and startingDay.
            arr = getParamsValues(pyear, pmonth, startingDay, yearInit, monthInit);
            if (typeof arr === 'string') return arr;    // error in the given params.
            //  Runs the calendarArray function from "calendarUtils" to get an array (w) corresponding to
            //  the year, month and startingDay.
            w = utils.calendarArray(arr[0], arr[1], arr[2]);
//            l = w.length;
            utils.rotateArray(arr[2], conf.days);
            //  Build the output string of the macro from the numerical array.
            str += "<table class='calendar-table'>";
            //  Title with or without buttons to change month and year
            str += calendarTitle(parsedYear, parsedMonth, arr[0], arr[1], conf);
            //  The row containing the abbreviated days names
            str+="<tr><th>"+conf.days.join("</th><th>")+"</th></tr>";
            //  The cells of the calendar
            str += calendarCells(w, name, conf);
            str += calendarBottom(name);
            str += "</table>";
            return str;
        }

    }
/* 
*   Fonctions for building the calendar table
*/

//  Builds the string for the first row of the calendar table : the title
//  with or without buttons.
//  There are three possible values for conf.buttons :
//  - "no" : no buttons
//  - "images" : images are displayed (those in conf.buttonsImages), 2 before the title, 2 after
//  - other values : buttons are displayed. They allow to change the month and year
//  (except when there are no states)

    function calendarTitle(parsedYear, parsedMonth, y, m, conf) {
        var s = "";
        var date, yearNow, monthNow;
        //  
        if (conf.buttons === "no") {
			s += "<th colspan='7'>" + conf.months[m - 1] + " " + y + "</th>";
//
        } else if (conf.buttons === "images") {
            s += "<tr><td>" + conf.buttonsImages[0] + "</td><td>" + conf.buttonsImages[1] + "</td>";
            s += "<th colspan='3'>" + conf.months[m - 1] + " " + y + "</th>";
            s += "<td>" + conf.buttonsImages[2] + "</td><td>" + conf.buttonsImages[3] +"</td></tr>";
//
        } else {
            s += "<tr>";
//  Button for previous year.
			s += "<td class='tc-popup-handle'><$button class='tc-btn-invisible'>" + conf.buttonsImages[0];
            s += setStateValue(parsedYear, y - 1);
            s += "</$button></td>";
//  Button for previous month.
            s += "<td class='tc-popup-handle'><$button class='tc-btn-invisible'>" + conf.buttonsImages[1];
            if (m == 1) {
                s += setStateValue(parsedMonth, 12);
                s += setStateValue(parsedYear, y - 1);
            } else s += setStateValue(parsedMonth, m - 1);
            s += "</$button></td>";
//  Title.
            s += "<th colspan='3' class='tc-popup-handle'>";
            s += "<$button class='tc-btn-invisible'>" + conf.months[m - 1] + " " + y;
            date = new Date();
            yearNow = date.getFullYear();
            monthNow = date.getMonth() +1;
            s += setStateValue(parsedYear, yearNow);
            s += setStateValue(parsedMonth, monthNow);
            s += "</$button></th>";
//  Button for next month.
            s += "<td class='tc-popup-handle'><$button class='tc-btn-invisible'>" + conf.buttonsImages[2];
            if (m == 12) {
                s += setStateValue(parsedMonth, 1);
                s += setStateValue(parsedYear, y + 1);
            } else s += setStateValue(parsedMonth, m + 1);
            s += "</$button></td>";
//  Button for next year.
			s += "<td class='tc-popup-handle'><$button class='tc-btn-invisible'>" + conf.buttonsImages[3];
            s += setStateValue(parsedYear, y + 1);
            s += "</$button></td>";
//
			s+="</tr>";
        }
        return s;
    }

    function setStateValue(parsedState, value) {
        var s = "<$action-setfield ";
        if (parsedState.title) s += "$tiddler=" + parsedState.title;
        if (parsedState.field) s += " $field=" + parsedState.field;
        s += " $value=" + value + "/>";
        return s;
    }

    function calendarCells(w, name, conf) {
        var s = "", l = w.length;
        for (var i = 0; i < l; i++) {
            s += "<tr>";
            for (var j = 0; j<7; j++) {
                s += "<td class=<<calendarCellCSS " + name + " " + w[i][j][0] +" ";
                s += w[i][j][1] + " " +w[i][j][2] + " " + w[i][j][3] + ">>>";
                s += "<<" + conf.cellContent + " " + w[i][j][0] + " ";
                s += w[i][j][1] + " " +w[i][j][2] + " " + w[i][j][3] + " '" + conf.format + "'>></td>";
            }
            s += "</tr>";
        }
        return s;
    }

    function calendarBottom(name) {
		var title = "$:/simplecalendar/" + name + "_Days";
        var s = "<tr class='calendar-bottom'>";
        s += "<td colspan='3' class='calendar-bottom-left'>";
        s += "<$button class='tc-btn-invisible' tooltip='Days'>";
        s += "<$action-setfield $tiddler='" + title + "' calendar='" + name +"' ";
		s += "text='{{||$:/plugins/frd/simplecalendar/DaysTemplate}}'/>";
		s += "<$action-navigate $to='" + title + "'/>&nbsp;</$button>";
        s += "</td><td class='calendar-bottom-center'>";
        s += "</td><td colspan='3' class='calendar-bottom-right'>";
        s += "<$button to='$:/plugins/frd/simplecalendar/CalendarHelp' class='tc-btn-invisible' tooltip='CalendarHelp'>&nbsp;</$button>";
		s += "</td></tr>";
        return s;
    }

/* 
*   Fonctions for getting values of parameters from state, and probing the values
*/

//  When a text reference is 'parsed' with $tw.utils.parseTextReference, the result is an object
//  with 2 fields (if they are not empty) : title (the title of the tiddler), and field (the field
//  where the state lives).
//  This function deals with the differents cases and returns the value of the state as a string.

    function getValueFromParsed(widget, parsed) {
        var res = "", tiddler;

        //  If there is a field, the value of the field of the tiddler (or current tiddler 
        //  if there is no title field) is returned
        if (parsed.field) {
            tiddler = $tw.wiki.getTiddler(
                parsed.title ? parsed.title : widget.getVariable("currentTiddler"));
			if (tiddler === undefined) {
				res = "";
			} else {
				res = tiddler.getFieldString(parsed.field); //  returns "" if missing field
			//  if there is no field, the text field of the tiddler is returned.
			}
		} else {
            res = $tw.wiki.getTiddlerText(parsed.title,"");
			}
		return res;
    }

//  This functions gets the numerical values of year, month and dayNum from the states
//  and the init values. Returns an array of 3 numbers : year, month and dayNum.

    function getParamsValues(pyear, pmonth, startingDay, yearInit, monthInit) {
        var a = [];
        var y, m;

		y = pyear;
		m = pmonth;
        //  If there is no state for the year or the state is empty, we take yearInit if not empty.
        //  If empty, we take the current year.
        if (y === "") y = yearInit;
        a[0] = probeAndParseData(y, "year");
        if (a[0] === false) return
                "Bad year value or bad range in yearState or yearInit" + pyear + " " + yearInit;
        //  Idem for the month
        if (m === "") m = monthInit;
        a[1] = probeAndParseData(m, "month");
        if (a[1] === false) return 
                "Bad month value or bad range in monthState or monthInit" + pmonth + " " + monthInit;
        a[2] = probeAndParseData(startingDay, "startingDay");
        if (a[2] === false) return "Bad value or bad range in startingDay : " +startingDay;

        return a;
    }

//  This functions gets the numerical value from the string and tests whether
//  the value is a number and is inside the right range.

    function probeAndParseData(data, field) {
        var num = parseInt(data, 10);
        if (isNaN(num)) num = false;
        else {
            if ((field === "year") && ((num < 999) || (num > 9999))) num = false;
            else if ((field === "month") && ((num < 1) || (num > 12))) num = false;
            else if ((field === "startingDay") && ((num < 0) || (num > 6))) num = false;
        }
        return num;
    }


})();
